mapper.insert:
insert into product(
	product_no, product_name, product_unit, product_price, seller_no, product_date
)
values(
#{product_no}, #{product_name},  #{product_unit}, #{product_price}, #{seller_no}, sysdate
)

insert into orderproduct(
	order_no, product_no,	member_id,	order_date, order_total
)values(
	#{order_no}, #{product_no}, #{member_id}, sysdate, #{order_total}
)
