insert.foreach:
into orderproduct(
	order_no, product_no, member_id,	order_date, order_price
	)
values(
	#{list.order_no}, #{list.product_no}, #{list.member_id}, sysdate, #{list.order_price}
	)

mapper.select:
		select  DISTINCT op.order_no, p.product_no, op.member_id,  p.product_name, p.product_price 
  		from product p, orderproduct op 
  		where p.product_no = op.product_no and member_id=#{member_id}
